package frc.robot.commands;

import frc.robot.sensors.RomiGyro;
import frc.robot.subsystems.Drivetrain;

import edu.wpi.first.wpilibj2.command.CommandBase;

public class yAxisStabilization extends CommandBase {
  private final Drivetrain m_drive;
  // private final double m_distance;
  private double m_speed;
  private boolean m_stable;
  private boolean m_stabilised;
  private int m_Tim;
  private final RomiGyro m_gyro=new RomiGyro();
  /**
   * CreatesanewDriveDistance.Thiscommandwilldriveyouryourrobotforadesireddistanceat
   * adesiredspeed.
   *
   * @param speed Thespeedatwhichtherobotwilldrive
   * @param inches Thenumberofinchestherobotwilldrive
   * @param drive Thedrivetrainsubsystemonwhichthiscommandwillrun
   */
  public yAxisStabilization(double speed,Boolean stable,Boolean stabilised,Integer Tim,Drivetrain drive){
    // m_distance=inches;
    m_speed=speed;
    m_stable=stable;
    m_stabilised=stabilised;
    m_Tim=Tim;
    m_drive=drive;
    addRequirements(drive);
  }

  //Calledwhenthecommandisinitiallyscheduled.
  @Override
  public void initialize(){
    m_drive.arcadeDrive(0,0);
    m_drive.resetEncoders();
  }

  //Calledeverytimetheschedulerrunswhilethecommandisscheduled.
  @Override
  public void execute(){

    if (m_stable){
      m_Tim=m_Tim+1;
        }
    if (m_stable=false){
            m_Tim=0;
            m_stabilised=false;
            m_stable=true;

    }
    if (5+0 <= m_Tim ) {
      m_stabilised=true;
    }
    if (m_gyro.getAngleY()<-5){
      m_speed=0.5;

      m_stable=false;
    }
    if (m_gyro.getAngleY()>5){
      m_speed=-0.5;
      // m_Tim=0;
      m_stable=false;
    }
                                                                                                                                                                        
       m_drive.arcadeDrive(m_speed, 0);
  }

  // Calledoncethecommandendsorisinterrupted.
  @Override
  public void end(boolean interrupted){
    m_drive.arcadeDrive(0,0);
  }

  // Returnstruewhenthecommandshouldend.
  @Override
  public boolean isFinished(){
    // Comparedistancetravelledfromstarttodesireddistance
    // returnMath.abs(m_drive.getAverageDistanceInch())>=m_distance;
    return m_stabilised;
  }
}
